class Solution {
public:
    bool dfs(vector<vector<char>>& grid, vector<vector<bool>>& vis, pair<int, int> parent, pair<int, int> grandparent, int m, int n, int i, int j, int depth) {
        if (vis[i][j]) {
            if (depth >= 4) {
                return true;
            }
            return false;
        }

        vis[i][j] = true;
        int dx[] = {-1, 1, 0, 0};
        int dy[] = {0, 0, -1, 1};

        for (int k = 0; k < 4; k++) {
            int ni = i + dx[k];
            int nj = j + dy[k];

            if (ni >= 0 && ni < m && nj >= 0 && nj < n && grid[ni][nj] == grid[i][j] && !check({ni, nj}, parent) && !check({ni, nj}, grandparent)) {
                if (dfs(grid, vis, {i, j}, parent, m, n, ni, nj, depth + 1)) {
                    return true;
                }
            }
        }

        return false;
    }

    bool containsCycle(vector<vector<char>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        vector<vector<bool>> vis(m, vector<bool>(n, false));

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (!vis[i][j]) {
                    if (dfs(grid, vis, {-1, -1}, {-1, -1}, m, n, i, j, 1)) {
                        return true;
                    }
                }
            }
        }

        return false;
    }

    bool check(pair<int, int> a, pair<int, int> b) {
        return a.first == b.first && a.second == b.second;
    }
};

