from queue import SimpleQueue

class Solution:
    def containsCycle(self, grid: List[List[str]]) -> bool:

        rows, cols = len(grid), len(grid[0])
        visited = [[False] * cols for _ in range(0, rows)]

        q = SimpleQueue()

        for i in range(0, rows):
            for j in range(0, cols):
                if not visited[i][j]:
                    visited[i][j] = True
                    q.put((i, j, -1, -1))
                    if self.dfs(q, rows, cols, visited, grid):
                        return True

    def dfs(self, q, rows, cols, visited, grid):
        while q.qsize() > 0:
            row, col, src_row, src_col = q.get()

            for i in range(-1, 2):
                for j in range(-1, 2):
                    nrow, ncol = row + i, col + j
                    if(
                        abs(i) != abs(j) and
                        nrow >= 0 and 
                        ncol >= 0 and 
                        nrow < rows and 
                        ncol < cols and
                        grid[nrow][ncol] == grid[row][col]
                    ):
                        if not visited[nrow][ncol]:
                            q.put((nrow, ncol, row, col))
                            visited[nrow][ncol] = True
                        elif visited[nrow][ncol] and (nrow, ncol) != (src_row, src_col):
                            return True 

        return False

