class Solution:
    def containsCycle(self, grid: List[List[str]]) -> bool:
        rows = len(grid)
        cols = len(grid[0])
        visited = [[False for i in range(cols)] for j in range(rows)]
        for i in range(rows):
            for j in range(cols):
                if not visited[i][j]:
                    if self.is_cycle(grid, i, j, visited, grid[i][j], -1, -1):
                        return True
        return False
    
    def is_cycle(self, grid, x, y, visited, start_char, prev_x, prev_y):
        if x < 0 or x >= len(grid) or y < 0 or y >= len(grid[0]):
            return False
        if grid[x][y] != start_char:
            return False
        
        if visited[x][y]:
            return True
        
        visited[x][y] = True

        if x + 1 != prev_x and self.is_cycle(grid, x + 1, y, visited, start_char, x, y):
            return True
        if x - 1 != prev_x and self.is_cycle(grid, x - 1, y, visited, start_char, x, y):
            return True
        if y + 1 != prev_y and self.is_cycle(grid, x, y + 1, visited, start_char, x, y):
            return True
        if y - 1 != prev_y and self.is_cycle(grid, x, y - 1, visited, start_char, x, y):
            return True
        
        return False
    
    #O(mn) T, O(mn) S
