```CPP []
class Solution {
public:
    vector<vector<int>> diff = {{0, -1}, {-1, 0}, {0, 1}, {1, 0}}; // left up right down
    bool cycle = false;
    int i, tr, tc;

    bool dfs(vector<vector<char>>& grid, vector<vector<bool>> &visited, int r, int c, char oldcolor, int pr = -1, int pc = -1)
    {
        if(r<0 || r>=grid.size() || c<0 || c>=grid[0].size() || grid[r][c] != oldcolor || cycle == true)
            return true; // True or False doesn't matter cause dfs is not storing any value
        if(visited[r][c] == true){
            cycle = true;
            return true; // same here, True or False doesn't matter
        }

        visited[r][c] = true;
        for(auto &d : diff)
        {
            tr = r+d[0], tc = c+d[1];
            if(tr == pr && tc == pc)
                continue;
            dfs(grid, visited, tr, tc, oldcolor, r, c);
        }
        return cycle == true ? true : false; // only this one matter, it will happen at the very end 
    }

    bool containsCycle(vector<vector<char>>& grid) 
    {
        vector<vector<bool>> visited( grid.size(), vector<bool>(grid[0].size()) );

        for(int r=0; r<grid.size(); r++)
            for(int c=0; c<grid[0].size(); c++)
                if(!visited[r][c] && dfs(grid, visited, r, c, grid[r][c]) == true)
                    return true;

        return false;
    }
};
```
