class Solution:
    def containsCycle(self, grid: List[List[str]]) -> bool:
        row, col, cycle = len(grid), len(grid[0]), False
        visited = [ [False for _ in range(col)] for _ in range(row) ]

        def dfs(r:int, c:int, oldcolor:str, pr:int = -1, pc:int = -1):
            nonlocal cycle
            if r<0 or r>=row or c<0 or c>=col or grid[r][c] != oldcolor or cycle == True:
                return True # True or False doesn't matter cause dfs is not storing any value
            if visited[r][c] == True:
                cycle = True; return True # same here, True or False doesn't matter

            visited[r][c] = True
            for (x,y) in ((0, -1), (-1, 0), (0, 1), (1, 0)):
                tr, tc = r+x, c+y
                if tr == pr and tc == pc:
                    continue
                dfs(tr, tc, oldcolor, r, c)

            return True if cycle == True else False # only this one matter, it will happen at the end

        return next((True for r in range(row) for c in range(col) if visited[r][c] == 0 and dfs(r,c,grid[r][c]) == True), False)
