public:

    vector<pair<int,int>>movements = { {-1,0},{0,-1},{0,1},{1,0}  } ;
     bool IsCycle = false ; 

      void dfs( int x , int y , int px , int py ,  vector<vector<bool>>&visited ,vector<vector<char>>& grid, int nr , int nc  , char initialchar ){
            visited[x][y] = true ;
            for( auto movement : movements ) {
                int childx = movement.first + x ;
                int childy = movement.second + y ;
                if( childx >= 0 and childx <= nr-1 and childy >= 0 and childy <= nc-1 and  grid[childx][childy] == initialchar  ){
                    // valid cell hai 
                    if( visited[childx][childy] == true and (childx == px and childy == py)  ) continue ; 
                    if( visited[childx][childy] == true  ) { IsCycle = true ;  continue ; }
                     dfs( childx , childy , x , y , visited , grid , nr , nc , initialchar ) ;
                }
            }
            return ;   
      }

