class Solution:
    def containsCycle(self, grid: List[List[str]]) -> bool:
        wide = len(grid)
        length = len(grid[0])
        seen = [[False]*length for _ in range(wide)]
        def dfs(x,y,alpha,previous):
            if seen[x][y]:
                return True
            seen[x][y]= True
            re = False
            direct = [(0,-1),(0,1),(1,0),(-1,0)]
            for i,j in direct:
                n_x, n_y = x+i, y+j

                if previous and -1*i==previous[0] and -1*j ==previous[1]:
                    continue
                if n_x<0 or n_x>=wide or n_y<0 or n_y>=length:
                    continue
                if grid[n_x][n_y]!=alpha:
                    continue 
                if seen[n_x][n_y] and grid[n_x][n_y]==alpha:

                    return True
                re = re or dfs(n_x,n_y,alpha,(i,j))
                if re:
                    return re
                seen[n_x][n_y]= True
            return re
        for i in range(wide):
            for j in range(length):
                if not seen[i][j]:
                    if dfs(i,j,grid[i][j],None):
                        return True
        return False
