class Solution:

    def dfs(self, i, j, parent, grid, visited, count):
        visited.add((i, j))
        prev_row, prev_col = parent[0], parent[1]
        dirs = [[0,-1], [-1,0], [1,0], [0,1]]

        for d in dirs:
            row = i + d[0]
            col = j + d[1]

            if 0 <= row < len(grid) and 0 <= col < len(grid[0]) and grid[row][col] == grid[i][j] and (row,col) != parent:
                if (row,col) not in visited:
                    if self.dfs(row, col, (i, j), grid, visited, count + 1):
                        return True
                elif (row,col) in visited and count >= 4:  #Check for parent and cycle detection (Striver)
                    return True
        return False


