                    if(nrow>=0 && ncol>=0 && nrow<n && ncol<m 
                     && grid[nrow][ncol] == grid[i][j]){
                    int node1 = i*m + j;
                    int node2 = nrow*m + ncol;
                    pair<int,int>p1 = {node1,node2};
                    pair<int,int> p2 = {node2,node1};
                    if(st.count(p1) == 0 && st.count(p2)==0 
                     && ds.findUpar(node1) == ds.findUpar(node2)){
                        return true;
