class Solution {
public:
    vector<int>drom = {0,-1,0,1};
    vector<int>dcol = {-1,0,1,0};
    bool detect(int i, int j,vector<vector<int>>&vis, vector<vector<char>>&grid, int x, int y )
    {
        vis[i][j] = 1;
        for(int l = 0; l<4; l++)
        {
            long long int a = i+drom[l];
            long long int b = j+dcol[l];
            if(a>=0 and a<grid.size() and b>=0 and b<grid[0].size() and grid[a][b] == grid[i][j] and !(x == a and y == b))
            {
                if(vis[a][b] or detect(a,b,vis,grid,i,j))return true;
            }
        }

        return false;

    }
    bool containsCycle(vector<vector<char>>& grid) 
    {
        int n = grid.size();
        int m = grid[0].size();

        vector<vector<int>>vis(n,vector<int>(m,0));

        for(int i = 0; i<n; i++)
        {
            for(int j = 0; j<m; j++)
            {
                if(!vis[i][j])
                {
                    if(detect(i,j,vis,grid, -1, -1)) return true;
                }
            }
        }
        return false;
    }
};
