class Solution {
    int[][] dirs = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
    boolean[][] seen = new boolean[501][501];
    public boolean containsCycle(char[][] grid) {
        int m = grid.length, n = grid[0].length;
        for(int row = 0; row < m; row++){
            for(int col = 0; col < n; col++){
                if(!seen[row][col] && containsCycle(grid, row, col, grid[row][col], row, col)){
                        return true;
                }
            }
        }
        return false;
    }

    private boolean containsCycle(char[][] grid, int startRow, int startCol, char search,
     int row, int col){
        int m = grid.length, n = grid[0].length;

        if(row >= m || col >= n || row < 0 || col < 0 || grid[row][col] != search)
            return false;

        if(seen[row][col]) return seen[row][col];  

        seen[row][col] = true;

        char temp = grid[row][col];  
        grid[row][col] = '*';
        boolean result =  containsCycle(grid, startRow, startCol, search, row, col + 1) 
            || containsCycle(grid, startRow, startCol, search, row, col - 1)
            || containsCycle(grid, startRow, startCol, search, row + 1, col)
            || containsCycle(grid, startRow, startCol, search, row - 1, col);
        grid[row][col] = temp;
        return result;
    }
}

