class Solution {
    public boolean isSafe(int r, int c, int rows, int cols) {
        return r>=0 && r<rows && c>=0 && c<cols;
    }
    public boolean containsCycle(char[][] grid) {
        int rows=grid.length,cols=grid[0].length;
        int[][] dirs={{0,1},{1,0},{0,-1},{-1,0}};
        for(int i=0;i<rows;i++) {
            for(int j=0;j<cols;j++) {
                if(grid[i][j]>='a') {
                    Queue<int[]> qu=new LinkedList<>();
                    qu.add(new int[] {i,j});
                    char ch=grid[i][j];
                    while(!qu.isEmpty()) {
                        int[] rc=qu.poll();
                        int r=rc[0],c=rc[1];
                        if(grid[r][c]<'a') {
                            return true;
                        }
                        grid[r][c]-=26;
                        for(int[] dir:dirs) {
                            int r1=r+dir[0],c1=c+dir[1];
                            if(isSafe(r1,c1,rows,cols) && grid[r1][c1]==ch) {
                                qu.add(new int[] {r1,c1});
                            }
                        }
                    }
                }
            }
        }
        return false;
    }
}
