```
class Solution {
    Map<Integer, List<Integer>> graph = new HashMap<>();
    boolean foundCriticalEdge;
    int[][] dirs = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};
    int root = -1, time = 0, count = 0;
    int[] dfn; int[] low;

    public int minDays(int[][] grid) {
        if (noOfIsland(grid) != 1) return 0;
        if (count == 2) return 2;
        buildGraph(grid);                           //build graph
        int m = grid.length, n = grid[0].length;
        dfn = new int[m * n]; low = new int[m * n]; //Trajan's algorithm
        Arrays.fill(dfn, -1);
        tarjan(-1, root);                            //(parent, cur, id_time, visietd)
        return foundCriticalEdge ? 1 : 2;
    }

    private void tarjan(int prev, int cur) {
        if (foundCriticalEdge) return;
        dfn[cur] = low[cur] = ++time;
        for (int nei : graph.get(cur)) {
