Guys, Comment me if you have any questions, Approach is pretty similar to AP but we need to consider some corner cases as well, 
Case 1 : there is only one island and one land return 1
Case 2 : if no of islands are more than one -> in this case we need to return 0;
Case 3: there is only one island and two lands return 2;
Case 4:  if there is an AP present return 1;
Case 5:  No Island return 0;
All other cases:  return 2;


```
class Solution {
    void dfs(list<int>* adj, vector<bool>& visited, vector<int>& discTime, vector<int>& lowTime, int& ap, int parent, int u){
        visited[u] = true;
        static int time = 0;
        discTime[u] = lowTime[u] = ++time;

        list<int>:: iterator itr;

        for(itr = adj[u].begin(); itr != adj[u].end(); itr++ ){
            int v = *itr;

            if(!visited[v]){
                dfs(adj, visited, discTime, lowTime, ap, u, v);
                lowTime[u] = min(lowTime[u], lowTime[v]);

                if(lowTime[v] > discTime[u]){
                    ap++;
                }
            }
            else if(parent != v){
                lowTime[u] = min(lowTime[u], discTime[v]);
            }
        }
    }
public:
    int minDays(vector<vector<int>>& grid) {
        if(grid.size() == 0) return 0;

        int m = grid.size();
        int n = grid[0].size();
        int size = m*n;
        int count = 0;
        vector<int> disc(size, 0);
        vector<int> low(size, 0);
        vector<bool> visited(size, false);
        int ap = 0;
        list<int> adj[size];
        int ones = size;

        for(int i = 0; i < m; i++ ){
            for(int j = 0; j < n; j++  ){
                if(grid[i][j]){
                    if(i+1 < m && grid[i+1][j]){
                        adj[i*n + j].push_back((i+1)*n + j);
                        adj[(i+1)*n + j].push_back(i*n + j);
                    }
                    if(i-1 >= 0 && grid[i-1][j]){
                        adj[i*n + j].push_back((i-1)*n + j);
                        adj[(i-1)*n + j].push_back(i*n + j);
                    }
                    if(j+1 < n && grid[i][j+1]){
                        adj[i*n + j].push_back(i*n + (j+1));
                        adj[(i*n + (j+1))].push_back(i*n + j);
                    }
                    if(j-1 >= 0 && grid[i][j-1]){
                        adj[i*n + j].push_back(i*n + (j-1));
                        adj[(i*n + (j-1))].push_back(i*n + j);
                     }         
                }
                else{
                    visited[i*n + j] = true;
                    ones--;
                }
            }
        }

        // Case 1 : there is only one island and one land
        if(ones == 1) return 1;

        for(int i = 0; i < size; i++ ){
            if(!visited[i]){
               dfs(adj, visited, disc, low, ap, -1, i);
               count++;
            }
        }


        // Case 2: island are more than 1 
        if(count > 1) return 0;

        // Case 3: there is only one island and two lands
        if(ones == 2) return 2;

        // Case 4: if there is an AP present
        if(ap) return 1;

		// Case 5: No Island
        if(count == 0) return 0;

        // All other cases 
        return 2;


    }
};
