class Solution {
public:
    int dx[4]={-1,0,1,0};
    int dy[4]={0,1,0,-1};
    bool safe(int x,int y,int m,int n)
    {
        return (x>=0&&x<m&&y>=0&&y<n);
    }
    void travel(int i,int j,vector<vector<int> > &grid,vector<vector<int> > &visited)
    {
        int m=grid.size();
        int n=grid[0].size();
        queue<pair<int,int > >q;
        q.push({i,j});
        visited[i][j]=1;
        while(!q.empty())
        {
            auto p=q.front();
            q.pop();
            int x=p.first;
            int y=p.second;
            for(int k=0;k<4;k++)
            {
                int nx=x+dx[k];
                int ny=y+dy[k];

                if(safe(nx,ny,m,n)&&!visited[nx][ny]&&grid[nx][ny]==1)
                {
                    visited[nx][ny]=1;
                    q.push({nx,ny});
                }
            }
        }
        return ;
    }
    int bfs(vector<vector<int> > &grid)
    {
        //number of connected component
        int count=0;
        int m=grid.size();
        if(m==0)
            return 0;
        int n=grid[0].size();
        vector<vector<int> > visited(m,vector<int> (n,0));
        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(grid[i][j]==1&&!visited[i][j])
                {
                    count++;
                    travel(i,j,grid,visited);
                }
            }
        }

        return count;
    }
    int minDays(vector<vector<int>>& grid) {
        int count=bfs(grid);
        if(count!=1)
            return 0;

        for(int i=0;i<grid.size();i++)
        {
            for(int j=0;j<grid[i].size();j++)
            {
                if(grid[i][j]==1)
                {
                    grid[i][j]=0;

                    count=bfs(grid);
                    if(count!=1)
                        return 1;

                    grid[i][j]=1;
                }
            }
        }

        return 2;
    }
};
