        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if(grid[i][j]==1 && vis[i][j]==false)
                {
                    count++;
                    queue<pair<int,int>>q;
                    vis[i][j]=true;
                    q.push(make_pair(i,j));
                    int dir[]={0,1,0,-1,0};
                    while(!q.empty())
                    {
                        auto p= q.front();
                        q.pop();
                        int x=p.first, y=p.second;
                        for(int k=0;k<4;k++)
                        {
                            int nx= x+dir[k];
                            int ny= y+dir[k+1];
                            if(nx>=0 && nx<n && ny>=0 && ny<m && grid[nx][ny] && !vis[nx][ny])
                            {
                                vis[nx][ny]=true;
                                q.push(make_pair(nx,ny));
                            }
                        }
                    }
                }
            }
        }
        return count;

    }

    int minDays(vector<vector<int>>& grid) {
        int conn= connected_components(grid);
        if(conn>1) return 0;

        int n=grid.size();
        if(n==0) return 0;
        int m=grid[0].size();

         for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if(grid[i][j]==1)
                {
                    grid[i][j]=0;
                    conn= connected_components(grid);
                    if(conn>1) return 1;
                    grid[i][j]=1;
                }
            }
        }
        return 2;
    }
};
