                                for(int k=0; k<4; k++){
                                    int row=r[k]+x, col=c[k]+y;
                                    if(isvalid(row, col, grid.size(), grid[0].size())){
                                        if(!chk[row][col] && v[row][col]){
                                            chk[row][col]=1;
                                            q.push({row, col});
                                        }
