       bool tarjin(vector<int> cur, vector<int> previous, vector<vector<int>>& grid, 
                vector<vector<int>>& depth, vector<vector<int>>& min_depth, int cur_depth) {
          if (depth[cur[0]][cur[1]]!=-1) return false;
          depth[cur[0]][cur[1]] = cur_depth;
          min_depth[cur[0]][cur[1]] = cur_depth;
          // check child nodes, i.e. the grid neighbors
          for (auto& next : nexts) {
            int next_x = cur[0]+next[0];
            int next_y = cur[1]+next[1];
            if (next_x<0 || next_x>=grid.size() || next_y<0 || next_y>=grid[0].size()) continue;
            if (grid[next_x][next_y] != 2) continue;
            // must ignore parent node.
            if (next_x == previous[0] && next_y == previous[1]) continue;  
            // calculate child nodes' min reachable depth.
            if (min_depth[next_x][next_y] == -1) { 
                if (tarjin({next_x, next_y},cur,grid,depth,min_depth,cur_depth+1)) {
                    return true;
                }
            }
            if (min_depth[next_x][next_y] > depth[cur[0]][cur[1]]) {
                // critical edge detected.
                return true;
            }

            min_depth[cur[0]][cur[1]] = min(min_depth[cur[0]][cur[1]], min_depth[next_x][next_y]);
        }
        return false;
       }


