        if (x<0 || x>=grid.size() || y<0 || y>=grid[0].size() || grid[x][y] != 1) return;
        grid[x][y] = 2;
        for (auto& next : nexts) {
           int next_x = x + next[0];
           int next_y = y + next[1];
           dfs(grid, next_x, next_y);
        }
