As the hints suggest, if the graph is already disconnected, then return 0.

if we can make the graph disconnected by removing one "1", return 1.
```
class Solution {
    vector<int> g;
    int find(int i) {
        if(g[i] == i) return i;
        g[i] = find(g[i]);
        return g[i];
    }

    void merge(int i, int j) {
        i = find(i);
        j = find(j);
        g[j] = i;
    }

public:
    int minDays(vector<vector<int>>& grid) {
        int m = grid.size(), n =grid[0].size();
        g.resize(m*n, 0);
        for(int i=0; i<m*n; ++i) g[i] = i;

        if(Disconnected(grid)) {
            return 0;
        }

        for(int i=0; i<m; i++) {
            for(int j=0; j<n; j++) {
                if(grid[i][j] == 0) continue;
                grid[i][j] = 0;
                if(Disconnected(grid)) return 1;
                grid[i][j] = 1;
            }
        }

        return 2;
    }

    bool Disconnected(vector<vector<int>>& grid) {
        int m = grid.size(), n =grid[0].size();
        g.resize(m*n, 0);
        for(int i=0; i<m*n; ++i) g[i] = i;

        for(int i=0; i<m; i++) {
            for(int j=0; j<n; j++) {
                if(grid[i][j] == 0) continue;
                if(i+1 < m && grid[i+1][j] == 1) merge(i * n + j, (i+1) * n + j);
                if(j+1 < n && grid[i][j+1] == 1) merge(i * n + j, i * n + j + 1);
            }
        }

        int res = -1;
        for(int i=0; i<m; i++) {
            for(int j=0; j<n; j++) {
                if(grid[i][j] == 0) continue;
                if(res == -1) {
                    res = find(i*n + j);
                } else if(res != find(i*n + j)) {
                    return true;
                }
            }
        }
        if(res == -1) return true;
        return false;
    }
};
