class Solution {
    private static final int[][] moves = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};

    public int minDays(int[][] grid) {
        if (isDisconnected(grid)) {
            return 0;
        } else if (isStrongConnected(grid)) {
            return 2;
        } else {
            return 1;
        }
    }

    private boolean isDisconnected(int[][] grid) {
        int answer = 0, n = grid.length, m = grid[0].length;
        boolean[][] visited = new boolean[n][m];

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (dfs(grid, visited, i, j)) {
                    answer++;
                }
            }
        }

        return answer != 1;
    }

    private boolean dfs(int[][] grid, boolean[][] visited, int y, int x) {
        if (y < 0 || y >= grid.length || x < 0 || x >= grid[y].length) {
            return false;
        }

        if (grid[y][x] == 0) {
            return false;
        }

        if (visited[y][x]) {
            return false;
        }
        visited[y][x] = true;

        for (int[] move : moves) {
            dfs(grid, visited, y + move[0], x + move[1]);
        }

        return true;
    }

    private boolean isStrongConnected(int[][] grid) {
        int n = grid.length, m = grid[0].length;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid[i][j] == 0) {
                    continue;
                }

                grid[i][j] = 0;
                if (isDisconnected(grid)) {
                    return false;
                }
                grid[i][j] = 1;
            }
        }

        return true;
    }
}
