public class Solution {
    public int MinDays(int[][] grid) {
        List<int[]> dirs = new List<int[]>() {
            new int[2] {1, 0},
            new int[2] {0, 1},
            new int[2] {-1, 0},
            new int[2] {0, -1}
        };

        if (GetNumberOfIslands(grid, dirs) > 1) {
            return 0;
        }

        for (int i = 0; i < grid.Length; i++) {
            for (int j = 0; j < grid[0].Length; j++) {
                if (grid[i][j] == 1) {
                    grid[i][j] = 0;

                    if (GetNumberOfIslands(grid, dirs) > 1) {
                        return 1;
                    }

                    grid[i][j] = 1;
                }
            }
        }

        return 2;
    }

    private int GetNumberOfIslands(int[][] grid, List<int[]> dirs) {
        bool[,] visited = new bool[grid.Length, grid[0].Length];
        int islandCount = 0;

        for (int i = 0; i < grid.Length; i++) {
            for (int j = 0; j < grid[0].Length; j++) {
                if (!visited[i, j] && grid[i][j] == 1)
                {
                    VisitIsland(grid, i, j, visited, dirs);
                    islandCount++;
                }
            }
        }

        return islandCount;
    }

    private void VisitIsland(int[][] grid, int i, int j, bool[,] visited, List<int[]> dirs) {
        visited[i, j] = true;
        foreach (int[] dir in dirs) {
            int nexti = i + dir[0];
            int nextj = j + dir[1];

            if (IsInBound(grid, nexti, nextj) && !visited[nexti, nextj] && grid[nexti][nextj] == 1)
            {
                VisitIsland(grid, nexti, nextj, visited, dirs);
            }
        }
    }

    private bool IsInBound(int[][] grid, int i, int j){
        return i >= 0 && j >= 0 && i < grid.Length && j < grid[0].Length;
    }
}
