using vi = vector<int>;
using vvi = vector<vi>;
using vvvi = vector<vvi>;
class Solution {
    vvvi ds = vvvi(30, vvi(30, vi(2, 0)));
    int n, m;
public:
    int minDays(vector<vector<int>>& grid) {
        n = grid.size(), m = grid[0].size();

        int islands = getIslands(grid);
        if (islands == 0 || islands > 1) return 0;

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (grid[i][j]) {
                    grid[i][j] = 0;
                    if (getIslands(grid) > 1) {
                        return 1;
                    }
                    grid[i][j] = 1;
                }
            }
        }

        return 2;
    }

private:
    int getIslands(vvi& grid) {
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                ds[i][j][0] = i * m + j, ds[i][j][1] = 0;
            }
        }

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (grid[i][j]) {
                    if (j < m - 1 && grid[i][j + 1]) {
                        unionSet(findSet(i, j), findSet(i, j + 1));
                    }
                    if (i < n - 1 && grid[i + 1][j]) {
                        unionSet(findSet(i, j), findSet(i + 1, j));
                    }
                }
            }
        }

        unordered_set<int> set;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (grid[i][j]) {
                    set.emplace(findSet(i, j));
                }
            }
        }

        return set.size();
    }

    void unionSet(int p1, int p2) {
        int i1 = p1 / m, j1 = p1 % m, i2 = p2 / m, j2 = p2 % m;
        if (ds[i1][j1][1] < ds[i2][j2][1]) {
            ds[i1][j1][0] = p2;
        } else {
            ds[i2][j2][0] = p1;
            if (ds[i1][j1][1] == ds[i2][j2][1]) {
                ++ds[i1][j1][1];
            }
        }
    }

    int findSet(int i, int j) {
        int p = ds[i][j][0];
        if (p == i * m + j) {
            return p;
        } else {
            return ds[i][j][0] = findSet(p / m, p % m);
        }
    }
};
