class Solution {

    private int m;
    private int n;
    private int[][] grid;

    private void dfs(int i, int j, boolean[] visited) {
        if (i < 0 || j < 0 || i == m || j == n || grid[i][j] == 0 || visited[i * n + j]) {
            return;
        }
        visited[i * n + j] = true;
        dfs(i + 1, j, visited);
        dfs(i - 1, j, visited);
        dfs(i, j + 1, visited);
        dfs(i, j - 1, visited);
    }

    private boolean disconnected() {
        boolean disconnected = false;
        boolean allZeros = true;
        boolean[] visited = new boolean[n * m];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 1 && !visited[i * n + j]) {
                    allZeros = false;
                    if (disconnected) {
                        return true;
                    }
                    dfs(i, j, visited);
                    disconnected = true;
                }
            }
        }
        return allZeros;
    }

    public int minDays(int[][] grid) {
        this.m = grid.length;
        this.n = grid[0].length;
        this.grid = grid;

        Deque<Integer> queue = new LinkedList<>();
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 1) {
                    queue.offer(i * n + j);
                }
            }
        }

        if (disconnected()) return 0;
        for (int k = queue.size(); k > 0; k--) {
            int index = queue.poll();
            int i = index / n;
            int j = index % n;
            grid[i][j] = 0;
            if (disconnected()) return 1;
            grid[i][j] = 1;
        }
        return 2;
    }
}
