class Solution {
public:
    int minDays(vector<vector<int>>& grid) {
        m = grid.size(), n = grid[0].size(), islands = 0, ones = 0;
        memset(visited, 0, sizeof(visited));

        for(int i=0; i<m; i++) for(int j=0; j<n; j++) {    // finding out #islands
            if(!visited[i][j] && grid[i][j] == 1)
                dfs(i, j, grid), islands++;
            if(grid[i][j] == 1) ones++;
        }

        if(islands != 1) return 0;     // islands = #Connected Components

        if(m == 1 || n == 1 || ones <= 2) return (ones == 2 ? 2: 1);   // #islands = 1

        for(int i=0; i<m; i++) for(int j=0; j<n; j++) if(grid[i][j] == 1) {
            int x = i, y = j; grid[i][j] = 0;

            memset(visited, 0, sizeof(visited));
            islands = 0;

             for(int i=0; i<m; i++) for(int j=0; j<n; j++) {     // finding out #islands
                if(!visited[i][j] && grid[i][j] == 1)
                    dfs(i, j, grid), islands++;
            }           

            if(islands > 1) return 1;    // we were able to disconnect the islands

            grid[x][y] = 1;
        }

        return 2;
    }

    void dfs(int i, int j, vector<vector<int>> &g) {    // visits all the 1s of 1 island, marks them visited
        visited[i][j] = 1; 

        if(i && !visited[i-1][j] && g[i-1][j] == 1) dfs(i-1, j, g);
        if(j && !visited[i][j-1] && g[i][j-1] == 1) dfs(i, j-1, g);
        if(i < m - 1 && !visited[i+1][j] && g[i+1][j] == 1) dfs(i+1, j, g);
        if(j < n - 1 && !visited[i][j+1] && g[i][j+1] == 1) dfs(i, j+1, g);
    }

private:
    int m, n, islands, ones, visited[35][35];
};
