func minDays(grid [][]int) int {
    m, n := len(grid), len(grid[0])
	visited := make([][]bool, m)
	for t := 0; t < m; t++ {
		visited[t] = make([]bool, n)
	}

	c := 0
	// check for answer 0
	// If already > 1 island then we dont need any days to disconnect them
	for i := 0; i < m; i++ {
		for j := 0; j < n; j++ {
			if grid[i][j] == 1 && !visited[i][j] {
				c++
				dfs(grid, i, j, m, n, visited)
			}
		}
	}

	if c > 1 {
		return 0
	}

	// check for 1
	// for all grid values of 1, make it 0 and check no of islands
	// if anytime you find > 1 island, we need 1 day to disconnect them
	for i := 0; i < m; i++ {
		for j := 0; j < n; j++ {
			if grid[i][j] == 1 {
				grid[i][j] = 0
				if findNoOfIslands(grid, i, j, m, n) > 1 {
					return 1
				}
				grid[i][j] = 1
			}
		}
	}

	// answer still not found , ans is 2 because
	// at max we need to turn only 2 cells (from 1 to 0) to disconnect islands
    return 2

}

func findNoOfIslands(grid [][]int, i, j, m, n int) int {
	visited := make([][]bool, m)
	for t := 0; t < m; t++ {
		visited[t] = make([]bool, n)
	}
	c := 0
	for i := 0; i < m; i++ {
		for j := 0; j < n; j++ {
			if grid[i][j] == 1 && !visited[i][j] {
				c++
				dfs(grid, i, j, m, n, visited)
			}
		}
	}

	return c
}

func dfs(grid [][]int, i, j, m, n int, visited [][]bool) {

	if i < 0 || j < 0 || i >= m || j >= n || grid[i][j] == 0 || visited[i][j] {
		return
	}

	visited[i][j] = true

	dfs(grid, i-1, j, m, n, visited)
	dfs(grid, i, j+1, m, n, visited)
	dfs(grid, i+1, j, m, n, visited)
	dfs(grid, i, j-1, m, n, visited)

}

