class Solution {
public:

    vector<int> rows{-1,0,1,0};
    vector<int> cols{0,1,0,-1};
    int isSafe(vector<vector<int>>& grid, int i, int j,vector<vector<bool>>& visited){
        int row = grid.size();
        int col = row ? grid[0].size():0;
        return (i<row &&  i>=0 && j >=0 && j<col && (grid[i][j] == 1 && !visited[i][j]));
    }


    void dfs(vector<vector<int>>& grid, vector<vector<bool>>& visited, int i,int j){


        visited[i][j] = true;

        for(int k =0;k<4;++k){
            if(isSafe(grid,i+rows[k],j+cols[k],visited)){
                dfs(grid,visited,i+rows[k],j+cols[k]);
            }
        }


    }

    int noOfIslands(vector<vector<int>>& grid){
        int m = grid.size();
        int n = m ? grid[0].size() :0;

        assert(n>=0);
        int count =0;
        vector<vector<bool>> visited(m,vector<bool>(n,false));

        for(int i =0;i<m;++i){
            for(int j =0;j<n ;++j){
                if(visited[i][j] == false && grid[i][j] == 1){
                        dfs(grid,visited,i,j);
                        count++;
                }
            }
        }

        return count;
    }


    int minDays(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = m ? grid[0].size() :m;
        int islands = noOfIslands(grid);
        if(islands >1 or islands == 0){
            return 0;
        } else{
            for(int i =0;i<m;++i){
                for(int j =0;j<n ;++j){
                    if(grid[i][j]){
                        grid[i][j] =0;
                        int islands= noOfIslands(grid);
                        grid[i][j] =1;
                        if( islands> 1 or islands == 0){
                            return 1;
                        }
                    }
                }
            }
        }
        return 2;      

