# Key point: The return value can only be 0, 1 or 2
class Solution:
    def minDays(self, grid: List[List[int]]) -> int:
        N_ones = sum([sum(i) for i in grid])
        if(N_ones == 0):
            return 0
        nRows = len(grid)
        nCols = len(grid[0])

        # This function calculates the area of the first island that we encounter
        def count_island_area():
            for i in range(nRows):
                for j in range(nCols):
                    if(grid[i][j] == 1):
                        g = [a[:] for a in grid]
                        area = 1
                        q = deque()
                        q.append((i,j))
                        g[i][j] = 0
                        while(q):
                            r,c = q.popleft()
                            if(r-1 >= 0 and g[r-1][c] == 1):
                                area += 1
                                g[r-1][c] = 0
                                q.append((r-1,c))
                            if(r+1 < nRows and g[r+1][c] == 1):
                                area += 1
                                g[r+1][c] = 0
                                q.append((r+1,c))
                            if(c-1 >= 0 and g[r][c-1] == 1):
                                area += 1
                                g[r][c-1] = 0
                                q.append((r,c-1))
                            if(c+1 < nCols and g[r][c+1] == 1):
                                area += 1
                                g[r][c+1] = 0
                                q.append((r,c+1))
                        return area
        # If the area of the first island we encounter is smaller than the total area of the island, that means there are more than one island. So we return 0
        if(count_island_area() < N_ones):
            return 0
        # At this point, there is only one island. We try to replace a '1' in the island with '0' one by one, and see if we can end up getting two islands.
        else:
            for i in range(nRows):
                for j in range(nCols):
                    if(grid[i][j] == 1):
                        grid[i][j] = 0
                        # If there are two islands after replacing a '1' with '0', return 1
                        if(count_island_area() < N_ones - 1):
                            return 1 
                        grid[i][j] = 1
        return 2
