class Solution {
public:
    int timer = 0;
    bool ap = 0;
    void makeset(vector<int> &parent, vector<int> &siz)
    {
        for(int i = 0 ; i < siz.size() ; i++)
        {
            parent[i] = i;
            siz[i] = 1;
        }
    }
    int findparent(int node, vector<int> &parent)
    {
        if(parent[node] == node)
            return node;
        return parent[node] = findparent(parent[node], parent);
    }
    void Union(int u, int v, vector<int> &parent, vector<int> &siz)
    {
        int pu = findparent(u, parent);
        int pv = findparent(v, parent);
        if(pu == pv)
            return;
        if(siz[pv] > siz[pu])
        {
            parent[pu] = pv;
            siz[pv] += siz[pu];
        }
        else
        {
            parent[pv] = pu;
