class Solution {
public:
    int arr1[4]={1,-1,0,0};
    int arr2[4]={0,0,1,-1};
    void bfs(int x,int y,vector<vector<int>> &vis,vector<vector<int>> grid){
        queue<pair<int,int>> pq;
        vis[x][y]=1;
        pq.push({x,y});
        while(!pq.empty()){
            pair<int,int> p=pq.front();
            int cx=p.first;
            int cy=p.second;
            pq.pop();
            for(int i=0;i<4;i++){
                int nx=cx+arr1[i];
                int ny=cy+arr2[i];
                if(nx>=0 and ny>=0 and nx<grid.size() and ny<grid[0].size()){
                    if(vis[nx][ny]==0 and grid[nx][ny]==1){
                        pq.push({nx,ny});
                        vis[nx][ny]=1;
                    }
                }
            }
        }

    }
    int f=0;
    void dfs(int sx,int sy,int timer,vector<vector<int>> &lo,vector<vector<int>> &time,int parentx,int parenty,vector<vector<int>> grid,vector<vector<int>> &vis){
        timer++;
        time[sx][sy]=timer;
        lo[sx][sy]=timer;
        vis[sx][sy]=1;
        int child=0;
        // cout<<sx<<' '<<sy<<endl;
        for(int i=0;i<4;i++){
            int nx=arr1[i]+sx;
            int ny=arr2[i]+sy;
            if(nx>=0 and ny>=0 and nx<grid.size() and ny<grid[0].size() and grid[nx][ny]==1){
                if(parentx==nx and parenty==ny){
                    //extra case
                    continue;
                }
                else if(vis[nx][ny]==0){
                    //case0
                    dfs(nx,ny,timer,lo,time,sx,sy,grid,vis);
                    // cout<<nx<<' '<<ny<<endl;
                    // case2
                    lo[sx][sy]=min(lo[sx][sy],lo[nx][ny]);
                    //case3
                    if(nx==1 and ny==2){
                        // cout<<"toi";
                        // cout<<lo[nx][ny]<<" "<<time[sx][sy]<<endl;
                    }
                    if(lo[nx][ny]>=time[sx][sy] and parentx!=-1 and parenty!=-1){
                        f=1;
                    }
                    child++;
                }
                else{
                    // case 1
                    lo[sx][sy]=min(lo[sx][sy],time[nx][ny]);
                }
            }
        }  
        if(parentx==-1 and parenty==-1){
            if(child>1){
                // cout<<child<<endl;
                f=1;
            }
        }  
    }
    int minDays(vector<vector<int>>& grid) {
        int c=0;
        int n=grid.size();
        int m=grid[0].size();
        vector<vector<int>> vis(n,vector<int>(m,0));
        int sourcex,sourcey;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]==0 and grid[i][j]==1){
                    sourcex=i;
                    sourcey=j;
                    c++;
                    bfs(i,j,vis,grid);
                }
            }
        }
        int cnt=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j]==1){
                    cnt++;
                }
            }
        }
        vector<vector<int>> vis1(n,vector<int>(m,0));
        vector<vector<int>> lo(n,vector<int>(m,0));
        vector<vector<int>> time(n,vector<int>(m,0));
        if(c>1){
            return 0;
        }
        else{
            dfs(sourcex,sourcey,0,lo,time,-1,-1,grid,vis1);
            if(f==1){
                return 1;
            }
        }
        return min(cnt,2);
    }
};
