    void dfs(int r,int c,vector<vector<int>>& grid){
        if(r<0 or c<0 or r>=grid.size() or c>=grid[0].size() or grid[r][c]==0 or vis[r][c]==1 ) return;
        vis[r][c]=1;

        dfs(r+1,c,grid);
        dfs(r-1,c,grid);
        dfs(r,c+1,grid);
        dfs(r,c-1,grid);
    }
