        n=grid.size(),m=grid[0].size();
        memset(vis,0,sizeof(vis));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]==0 and grid[i][j]==1){
                    dfs(i,j,grid),islands++;
                }
                if(grid[i][j]==1) ones++;
