class Solution {
public:

    vector<vector<int>> low , disc ;
    vector<vector<bool>> arti;
    vector<vector<pair<int , int>>> par;

    int n , m;
    int time;

    int minDays(vector<vector<int>>& grid) {

        n = grid.size() ;
        m = grid[0].size();

        low.resize(n , vector<int> (m , -1));
        disc.resize(n , vector<int> (m , -1));
        par.resize(n , vector<pair<int , int>> (m , {-1 , -1}));
        arti.resize(n , vector<bool>(m , false));

        int flg_arti = 0;

        int conn = 0 , cnt = 0;
        for(int i = 0 ; i < n ; i++) {
            for(int j = 0 ; j < m ; j++) {
                if(grid[i][j] == 1 && disc[i][j] == -1) {
                    if(conn == 1) return 0;
                    conn++;
                    time = 0;
                    cnt += solve(i , j , grid);
                }
                if(arti[i][j]) {
                    flg_arti = 1;
                }
            }
        }
        if(cnt == 0) return 0;
        if(cnt == 1 || flg_arti) return 1;
        return 2;


    }

    int dx[4] = {1 , -1 , 0 , 0};
    int dy[4] = {0 , 0 , -1 , 1};


    int solve(int rw , int col , vector<vector<int>> &grid) {

        disc[rw][col] = low[rw][col] = time++;
        int child = 0;
        int cnt = 1;

        for(int i = 0 ; i < 4 ; i++) {
            int x = rw + dx[i];
            int y = col + dy[i];

            if(x >= 0 && x < n && y >= 0 && y < m && grid[x][y] == 1) {

                if(disc[x][y] == -1) {
                    par[x][y] = {rw , col};
                    child += 1;

                    cnt += solve(x , y , grid);

                    low[rw][col] = min(low[rw][col] , low[x][y]);

                    if(par[rw][col].first == -1 && par[rw][col].second == -1 && child >= 2) {
                        arti[rw][col] = true;
                    }
                    if(par[rw][col].first != -1 && par[rw][col].second != -1 && low[x][y] >= disc[rw][col]) {
                        arti[rw][col] = true;
                    } 
                }
                else if(make_pair(x , y) != par[rw][col]) {
                    low[rw][col] = min(low[rw][col] , disc[x][y]);
                }

            }
        }
        return cnt;

    }
};
