class Solution {
    int [][] disc;
    int [][] sat;
    int [][] parent;
    boolean [][] vis;
    int[][] dir = { { 1, 0 }, { -1, 0 }, { 0, -1 }, { 0, 1 } };
    int time=0;
    public int DFSSIZE(int [][] grid,int i ,int j ,boolean [][] vis){
        vis[i][j]=true;
        int count=0;
        for(int [] arr:dir){
            int r=arr[0]+i;
            int c=arr[1]+j;

            if(r>=0 && c>=0 && r<grid.length && c<grid[0].length && grid[r][c]==1 && !vis[r][c]){
                count+=DFSSIZE(grid,r,c,vis);
            }
        }
        return count+1;
    }

    public boolean AP(int [][] grid,int srcr,int srcc,int [][] parent){
        disc[srcr][srcc]=sat[srcr][srcc]=++time;
        int child=0;
        boolean res=false;
        for(int [] arr:dir){
            int nbrx=arr[0]+srcr;
            int nbry=arr[1]+srcc;
            int m=grid[0].length;

            if(nbrx>=0 && nbry>=0 && nbrx<grid.length && nbry<grid[0].length && grid[nbrx][nbry]==1){
                // System.out.println(nbrx+" "+nbry);
                if(vis[nbrx][nbry] && parent[srcr][srcc]==(nbrx*m)+nbry){

                    continue;
                }
                else if(vis[nbrx][nbry]){

                    sat[srcr][srcc]=Math.min(sat[srcr][srcc],disc[nbrx][nbry]);
                }
                else{
                    vis[nbrx][nbry]=true;
                    parent[nbrx][nbry]=(srcr*m)+srcc;
                    child++;
                    res=res || AP(grid,nbrx,nbry,parent);

                    sat[srcr][srcc]=Math.min(sat[srcr][srcc],sat[nbrx][nbry]);

                    if(parent[srcr][srcc]==-1){
                        if(child>=2){
                            return true;
                        }
                    }
                    else{
                        if(disc[srcr][srcc]<=sat[nbrx][nbry]){
                            return true;
                        }
                    }
                }
            }
        }
        return res; 
    }
    public int minDays(int[][] grid) {
        int size=0;
        int noofComponents=0;
        int n =grid.length;
        int m =grid[0].length;
        parent=new int[n][m];
        sat=new int[n][m];
        disc=new int[n][m];
        vis=new boolean[n][m];
        int rootx=0;
        int rooty=0;
        boolean res=false;
        for(int i =0;i<n;i++){
            for(int j =0;j<m;j++){
                if(!vis[i][j] && grid[i][j]==1){
                    rootx=i;
                    rooty=j;
                    size+=DFSSIZE(grid,i,j,vis);
                   noofComponents++; 
                }
            }
        }
        vis=new boolean [n][m];
        if(noofComponents==0 || noofComponents>1){
            return 0;
        }
        else {
            if(size==1 || size==2){
                return size;
            }
            else{
                parent[rootx][rooty]=-1;
               vis[rootx][rooty]=true;
               res=AP(grid,rootx,rooty,parent); 

                if(res){
                    return 1;
                }
                else{
                    return 2;
                }
            }
        }

    }
}
