        //first land mass
        for(int i=0;i<n;i++){
        for(int j=0;j<m;j++){
            if(grid[i][j]==1){
                root=i*m+j;
                break;
            }
        }
        if(root!=-1)
        break;
        }
       //articulation point 
        boolean ans=arti(root,-1,grid,len,n,m);

        // vis will not be true on all1s if their are already multiple components
        for(int i=0;i<(n*m);i++)
        if(grid[i/m][i%m]==1 && vis[i]==false){
          return 0;
