        // array for traversing in 4 directions
        int[][] dir={{ 1, 0 }, { -1, 0 }, { 0, -1 }, { 0, 1 }};
        for(int[] d: dir){
            int r=sr+d[0];
            int c=sc+d[1];
            int child=r*m+c;
            //if thier is land in child coordinates
            if(r>=0 && r<n && c>=0 && c<m && grid[r][c]==1){
                //logic of articulation point refer youtube video
                // consider love babber code help placement series lecture 99
                if(!vis[child]){
                    com++;
                    res=arti(child,root,grid,len,n,m)||res;
                    low[root]=Math.min(low[root],low[child]);

