public class Solution {
    public int MinDays(int[][] grid) {
        int m = grid.Length, n = grid[0].Length;

        bool isLand(int x, int y)
        {
            if(x < 0 || x >= m || y < 0 || y >= n || (grid[x][y] != 1))
                return false;
            return true;
        }
        bool isConnected()
        {
            bool[,] visited = new bool[m, n];
            bool visit(int x, int y)
            {
                if((!isLand(x, y)) || visited[x, y])
                    return false;
                visited[x, y] = true;
                visit(x - 1, y);
                visit(x + 1, y);
                visit(x, y - 1);
                visit(x, y + 1);
                return true;
            }
            int visitedCnt = 0;
            for(int i = 0; i < m; i++)
                for(int j = 0; j < n; j++)
                    visitedCnt += visit(i, j)?1:0;            
            return visitedCnt == 1;
        }

        //already disconnected
        if(!isConnected())
            return 0;

        //one single operation required: try deleting any land tile
        for(int i = 0; i < m; i++)
            for(int j = 0; j < n; j++)
                if(isLand(i, j))
                {
                    grid[i][j] = 0;
                    if(!isConnected())
                        return 1;
                    grid[i][j] = 1;
                }

        //all other cases:
        return 2;
    }
}
