class Solution {
public:
    void helper(int i,int j,int n,int m,vector<vector<int>>&arr,vector<vector<bool>>&visited){

        if(i<0 || j<0 || i>=n || j>=m || arr[i][j]==0 || visited[i][j]==true){
            return;
        }
        visited[i][j]=true;
        helper(i+1,j,n,m,arr,visited);
        helper(i,j-1,n,m,arr,visited);
        helper(i-1,j,n,m,arr,visited);
        helper(i,j+1,n,m,arr,visited);

    }
    int numislands(int n,int m,vector<vector<int>>&arr){

        vector<vector<bool>> visited(n,vector<bool>(m,false));
        long long count=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(arr[i][j]==1 && visited[i][j]!=true){
                    helper(i,j,n,m,arr,visited);
                    count++;
                }
            }
        }
        return count;
    }
    int minDays(vector<vector<int>>& grid) {

        int n=grid.size();
        int m=grid[0].size();
        int ans=numislands(n,m,grid);
        if(ans>1 || ans==0){
            return 0;
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){

                if(grid[i][j]==1){
                    grid[i][j]=0;
                    int num=numislands(n,m,grid);
                    if(num>1 || num==0){
                        return 1;
                    }
                    grid[i][j]=1;
                }
            }
        }
        return 2;
    }
};

