impl Solution {
    pub fn min_days(grid: Vec<Vec<i32>>) -> i32 {
        let mut grid = grid;
        let mut islands = Self::count_islands(&grid);
        let (n, m) = (grid.len(), grid[0].len());
        if islands > 1 || islands == 0 {
            return 0;
        } else {
            for i in 0..n {
                for j in 0..m {
                    if grid[i][j] == 1 {
                        grid[i][j] = 0;
                        islands = Self::count_islands(&grid);
                        grid[i][j] = 1;
                        if islands > 1 || islands == 0 {
                            return 1;
                        }
                    }
                }
            }
        }
        2
    }

    fn dfs(x: usize, y: usize, grid: &Vec<Vec<i32>>, vis: &mut Vec<Vec<i32>>) {
        let dx = vec![1, -1, 0, 0];
        let dy = vec![0, 0, 1, -1];
        let (n, m) = (grid.len(), grid[0].len());
        vis[x][y] = 1;
        for a in 0..4 {
            let nx = x as i32 + dx[a];
            let ny = y as i32 + dy[a];
            if nx >= 0
                && ny >= 0
                && nx < n as i32
                && ny < m as i32
                && vis[nx as usize][ny as usize] == 0
                && grid[nx as usize][ny as usize] == 1
            {
                Self::dfs(nx as usize, ny as usize, grid, vis);
            }
        }
    }

    fn count_islands(grid: &Vec<Vec<i32>>) -> i32 {
        let mut islands = 0;
        let (n, m) = (grid.len(), grid[0].len());
        let mut vis = vec![vec![0; m]; n];
        for i in 0..n {
            for j in 0..m {
                if vis[i][j] == 0 && grid[i][j] == 1 {
                    Self::dfs(i, j, grid, &mut vis);
                    islands += 1;
                }
            }
        }
        islands
    }
}
