import random
class Solution:
    def minDays(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])

        def is_valid(i, j):
            return 0 <= i < m and 0 <= j < n

        def get_neis(i, j):
            return [(i+1,j), (i-1,j), (i,j+1), (i,j-1)]

        def dfs(i, j, visited):
            visited.add((i,j))
            for x, y in get_neis(i, j):
                if is_valid(x, y) and (x,y) not in visited and grid[x][y] == 1:
                    dfs(x, y, visited)

        islands = []
        seen = set()
        for i in range(m):
            for j in range(n):
                if (i,j) not in seen and grid[i][j] == 1:
                    visited = set()
                    dfs(i, j, visited)
                    islands.append(visited)
                    seen.update(visited)

        if len(islands) > 1 or len(islands) == 0:
            return 0

        island_indices = islands[0]
        if len(island_indices) == 1:
            return 1

        island_copy = copy.copy(island_indices)
        for index in island_indices:
            i, j = index
            island_copy.remove(index)
            grid[i][j] = 0
            visited = set()
            x, y = random.choice(list(island_copy))
            dfs(x, y, visited)
            if len(visited) != len(island_copy):
                return 1

            island_copy.add(index)
            grid[i][j] = 1

        return 2

