from itertools import product

class Solution:
    def minDays(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        def connect(grid):
            visited, area = set(), 0
            def dfs(x, y):
                for nx, ny in [(x + 1, y), (x - 1, y), (x, y + 1), (x, y - 1)]:
                    if 0 <= nx < m and 0 <= ny < n and grid[nx][ny] and (nx, ny) not in visited:
                        visited.add((nx, ny))
                        dfs(nx, ny)
            for x, y in product(range(m), range(n)):
                if grid[x][y] and (x, y) not in visited:
                    if area: return False
                    area += 1
                    visited.add((x, y))
                    dfs(x, y)
            return area == 1
        if not connect(grid): return 0
        for x, y in product(range(m), range(n)):
                if grid[x][y]:
                    grid[x][y] = 0
                    if not connect(grid): return 1
                    grid[x][y] = 1
        return 2
