class Solution {
public:
    vector<pair<int,int>>v;
    int count1 = 0;
    void dfs(int i,int j,pair<int,int>parent,int timer,vector<vector<int>>& grid,
             vector<vector<int>>&visited,vector<vector<int>>&tin,vector<vector<int>>&low)
    {
        int n = grid.size(),m=grid[0].size();
        visited[i][j] = 1;
        tin[i][j] = low[i][j] = timer++;
        count1++;
        //cout<<i<<" "<<j<<endl;
        vector<int>dx = {0,1,0,-1};
        vector<int>dy = {1,0,-1,0};
        int child = 0;
        for(int k=0;k<4;k++)
        {
            int x = i+dx[k];
            int y = j+dy[k];
            if(x<0 || y<0 || x>=n || y>=m || grid[x][y] == 0)
                    continue;

            else if((x == parent.first && y==parent.second))
                continue;

            else if(!visited[x][y])
            {
                dfs(x,y,{i,j},timer,grid,visited,tin,low);
                low[i][j] = min(low[x][y] , low[i][j]);
                if(low[x][y] >= tin[i][j] && (parent.first != -1 && parent.second != -1))
                {
                    v.push_back({i,j});
                    //cout<<i<<" "<<j;
                }
                child++;    

            }
            else if(visited[x][y])
                low[i][j] = min(tin[x][y] , low[i][j]);


        }
        if(parent.first == -1 && parent.second == -1 && child >= 2)
        {
             v.push_back({i,j});
             //cout<<i<<" "<<j;
        }


    }
    int minDays(vector<vector<int>>& grid) {
        int n = grid.size(),m=grid[0].size();
        vector<vector<int>>visited(n,vector<int>(m,0));
        vector<vector<int>>tin(n,vector<int>(m,0));
        vector<vector<int>>low(n,vector<int>(m,0));

        int count = 0;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                if(grid[i][j] == 1 && !visited[i][j])
                {
                    dfs(i,j,{-1,-1},1,grid,visited,tin,low);
                    count++;
                    if(count >= 2)
                        return 0;
                }
            }
        }
        if(count == 0) 
            return 0;

        if(count1 == 1 || (count == 1 && v.size() > 0 ) )
            return 1;

        return 2;
    }
};
