int dx[4] = {-1, 1, 0, 0}, dy[4] = {0, 0, -1, 1};

    bool check(vector < vector <int> > &grid, int i, int j, int m, int n) {
        return (i >= 0 && i < m && j >= 0 && j < n && grid[i][j] == 1);
    }

    int solve(vector < vector <int> > &grid) {
        int m = grid.size(), n = grid[0].size(), ans = 0;
        vector < vector <bool> > vis(m, vector <bool>(n, false));

        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                if(grid[i][j] && !vis[i][j]) {
                    ans++;
                    queue < pair <int, int> > q;
                    q.push({i, j});
                    vis[i][j] = true;

                    while(!q.empty()) {
                        auto [ux, uy] = q.front();
                        q.pop();
                        for(int k = 0; k < 4; k++) {
                            if(check(grid, ux + dx[k], uy + dy[k], m, n) && !vis[ux + dx[k]][uy + dy[k]]) {
                                q.push({ux + dx[k], uy + dy[k]});
                                vis[ux + dx[k]][uy + dy[k]] = true;
                            }
                        }
                    }
                }
            }
        }
      return ans;
    }

    int minDays(vector<vector<int>>& grid) {

        int ans = solve(grid), m = grid.size(), n = grid[0].size();
        if(ans != 1)
            return 0;

        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                if(grid[i][j]) {
                    grid[i][j] = 0;
                    int val = solve(grid);
                    grid[i][j] = 1;

                    if(val != 1)
                        return 1;
                }
            }
        }

        return 2;     
    }
