class Solution {
public:
    int minDays(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        vector<int>adj[n*m];
        int dif[]={0,1,0,-1,0};
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(grid[i][j]){
                    for(int k=0;k<4;k++){
                        int r=i+dif[k],c=j+dif[k+1];
                        if(r>=0 && r<n && c>=0 && c<m && grid[r][c]){
                            adj[i*m+j].push_back(r*m+c);
                            adj[r*m+c].push_back(i*m+j);
                        }
                    }
                }
            }
        }
    vector<int> tin(n*m, -1);
	vector<int> low(n*m, -1); 
	vector<int> vis(n*m, 0);
	vector<int> isArticulation(n*m, 0); 
	int timer = 0; 
        int land=0;
        int bodies=0;
	for(int i = 0;i<n;i++) {
        for(int j=0;j<m;j++){
	    if(grid[i][j]) {
            land++;
            if(!vis[i*m+j]){
                bodies++;
                dfs(i*m+j, -1, vis, tin, low, timer, adj, isArticulation); 
            }

	    }
        }
	}
        cout<<land<<endl;
	if(bodies==0 || bodies>1) return 0;
        int art=0;
	for(int i = 0;i<n*m;i++) {
	    if(isArticulation[i] == 1) art++;
	}
    if(art || land==1) return 1;

        return 2;

    }
    void dfs(int node, int parent, vector<int> &vis, vector<int> &tin, vector<int> &low, int &timer, vector<int> adj[], vector<int> &isArticulation) {
    vis[node] = 1; 
    tin[node] = low[node] = timer++;
    int child = 0; 
    for(auto it: adj[node]) {
        if(it == parent) continue;

        if(!vis[it]) {
            dfs(it, node, vis, tin, low, timer, adj, isArticulation); 
            low[node] = min(low[node], low[it]); 
	    child++; 
            if(low[it] >= tin[node] && parent != -1) {
                isArticulation[node] = 1; 
            }
        } else {
            low[node] = min(low[node], tin[it]); 
        }
    }

    if(parent == -1 && child > 1) {
        isArticulation[node] = 1; 
    }
}
};
