public class Solution {
    int m,n;
    bool[,] visited;
    int[,] id;
    int[,] low;
    int[][] grid;
    int numOfOutEdge=0;
    bool[,] art;
    int numOfComponents=0;
    (int di,int dj)[] dir=new (int di,int dj)[4]{(1,0),(-1,0),(0,1),(0,-1)};
    int idc=0;
    int artcount=0;
    public int MinDays(int[][] grid) {
        m=grid.Length;
        n=grid[0].Length;
        visited=new bool[m,n];
        low=new int[m,n];
        id=new int[m,n];
        art=new bool[m,n];
        this.grid=grid;

        for(int i=0;i<m;i++)
            for(int j=0;j<n;j++)
            {
                numOfOutEdge=0;
                if(!visited[i,j] &&  grid[i][j]==1){

                    dfs((i,j),(i,j),(-1,-1) );
                    if(art[i,j] && numOfOutEdge<=1){
                        artcount--;
                        art[i,j]=false;
                    }
                    numOfComponents++;
                }
                if(numOfComponents>1)
                    return 0;


            }
            if(artcount>0)
                    return 1;
                else 
                    return Math.Min(2,idc);


    }

    public void dfs((int i,int j) root, (int i,int j) at,(int i,int j) parent)
    {

        if((root.i==parent.i ) && (root.j==parent.j))
            numOfOutEdge++;
        idc++;
        id[at.i,at.j]=low[at.i,at.j]=idc;
        visited[at.i,at.j]=true;

        for(int d=0;d<4;d++)
        {
            (int i,int j) next=(at.i+dir[d].di,at.j+dir[d].dj);
            if(!isValidNode(next) || (next.i==parent.i && next.j==parent.j) || grid[next.i][next.j]!=1)
                continue;

            if(visited[next.i,next.j]){
                low[at.i,at.j]=Math.Min(low[at.i,at.j],id[next.i,next.j]);

            }
            else
            {
                dfs(root,next,at);
                low[at.i,at.j]=Math.Min(low[at.i,at.j],low[next.i,next.j]);
                if(low[next.i,next.j]>=id[at.i,at.j]){
                    art[at.i,at.j]=true;
                    artcount++;
                }
            }
        }
    }

    bool isValidNode((int i,int j) node)
    {
        if(node.i>=m || node.i<0 || node.j>=n || node.j<0)
            return false;
        return true;

    }
}

