class Solution {
public:
    vector<pair<int, int>> steps = {{-1,0}, {1,0}, {0,-1}, {0,1}}; 

    bool check(int r, int c, int n, int m)
    {
        return (r>=0 && r<n && c>=0 && c<m);
    }

    void dfs(vector<vector<int>>& grid, int r, int c, vector<vector<bool>>& vis, vector<vector<int>>& disc, vector<vector<int>>& low, int &isAp, int &timer, vector<vector<pair<int, int>>>& parent, int parR, int parC, int n, int m)
    {
        vis[r][c] = true;
        disc[r][c] = low[r][c] = ++timer;
        parent[r][c] = {parR, parC};
        int child = 0;

        for(auto x: steps)
        {
            int a = r + x.first;
            int b = c + x.second;

            if(check(a, b, n, m) && grid[a][b] == 1)
            {
                if(!vis[a][b])
                {
                    child++;

                    dfs(grid, a, b, vis, disc, low, isAp, timer, parent, r, c, n, m);
                    low[r][c] = min(low[r][c], low[a][b]);

                    if((parent[r][c].first !=-1 && parent[r][c].second !=-1) && low[a][b] >= disc[r][c])
                        isAp = 1;
                }

                else if(a != parent[r][c].first || b != parent[r][c].second)
                {
                    low[r][c] = min(low[r][c], disc[a][b]);
                }
            }
        }

        if(child > 1 && parent[r][c].first == -1 && parent[r][c].second == -1)
            isAp = 1;
    }

    int minDays(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();

        int sum = 0;
        for(int i=0; i<n; i++)
            for(int j=0; j<m; j++)
            {
                if(grid[i][j])
                    sum++;
            }

        if(sum == 1)
            return 1;
        if(sum == 0)
            return 0;
        if(sum == m*n)
            return 2;

        vector<vector<bool>> vis(n, vector<bool>(m, false));
        vector<vector<int>> disc(n, vector<int>(m, 0));
        vector<vector<int>> low(n, vector<int>(m, 0));

        vector<vector<pair<int, int>>> parent(n, vector<pair<int,int>>(m));

        int timer = 0;
        int isAp = 0;

        int count = 0;
        for(int i=0; i<n; i++)
            for(int j=0; j<m; j++)
            {
                if(grid[i][j]==1 && !vis[i][j])
                {
                    count++;
                    if(count>1)
                        return 0;

                    dfs(grid, i, j, vis, disc, low, isAp, timer, parent, -1, -1, n, m);
                }
            }

        if(isAp == 1)
            return 1;

        return 2;
    }
};












