var minDays = function(grid) {
    let c=copy2Darray(grid);
    //console.log(c)
    let tmp=bfs(c);
    //console.log('xxxx', tmp)
    if(tmp>1 || tmp==0){//題意已經是分離的島了   
        return 0;
    }

    for(let i=0; i<grid.length; i++){
        for(let j=0; j<grid[0].length; j++){
            if(grid[i][j]==1){
                grid[i][j]=0;//設為0
                let clone=copy2Darray(grid);
                let res=bfs(clone);
                console.log(i,j,'-->', res)
                if(res != 1) //可以使島分成1個以上
                    return 1;
                grid[i][j]=1;//復原
            }
        }
    }
    return 2;

    function copy2Darray(a){
        let b=[];
        for(let i=0; i<a.length; i++){
            let t=[];
            for(let j=0; j<a[0].length; j++){
                t.push(a[i][j]);
            }
            b.push(t);
        }
        return b;
    }

    function bfs(gr){//使用flood fill, 檢查盤面上的島是一個或大於一個
        console.log(gr)
        let numberOfIslands=0;
        let dirs=[[-1,0],[1,0],[0,1],[0,-1]];
        for(let m=0; m<gr.length; m++){
            for(let n=0; n<gr[0].length; n++){
                if(gr[m][n]==1){
                    numberOfIslands++; //特別的地方
                    if(numberOfIslands>1)
                        return numberOfIslands;
                    gr[m][n]=0;
                    let nc=[[m,n]];
                    while(nc.length>0){
                        let nb=nc;
                        nc=[];
                        for(let [x,y] of nb){
                            for(let [i,j] of dirs){
                                if(x+i<0 || x+i>=gr.length || y+j<0 || y+j>=gr[0].length || gr[x+i][y+j]==0)
                                    continue;
                                gr[x+i][y+j]=0;
                                nc.push([x+i, y+j]);
                            }                                
                        }
                    }
                }                                   
            }
        }
        return numberOfIslands;
    }   
};
