class Solution {
  public int minDays(int[][] grid) {
    // return firstIncorrectAttempt(grid);
  }

  public int firstIncorrectAttempt(int[][] grid) {
    int[] res = smallestIsland(grid);
    int count = res[0];
    int size = res[1];
    if(count != 1) {
      return 0;
    }
    // Does not account for an island like this [[1,1,1,1,1]]
    if(size > 3) {
      return 2;
    } else {
      return size;
    }
  }

  public int[] smallestIsland(int[][] grid) {
    // returns {numsIslands, smallestIsland}
    int islandCount = 0;
    int size = Integer.MAX_VALUE;
    int[][] visited = new int[grid.length][grid[0].length];
    for(int i = 0; i < grid.length; i++) {
      for(int j = 0; j < grid[0].length; j++) {
        if(grid[i][j] == 1 && visited[i][j] != 1) {
          ++islandCount;
          size = Math.min(size, exploreIsland(grid, visited, i, j));
        }
      }
    }
    return new int[]{islandCount, size};
  }

  // returns island size
  public int exploreIsland(int[][] grid, int[][] visited, int i, int j) {
    if(i < 0 || j < 0 || i >= grid.length || j >= grid[0].length
      || visited[i][j] == 1 || grid[i][j] != 1) {
      return -1;
    }

    visited[i][j] = 1;

    int up = exploreIsland(grid, visited, i + 1, j);
    int down = exploreIsland(grid, visited, i - 1, j);
    int left = exploreIsland(grid, visited, i, j - 1);
    int right = exploreIsland(grid, visited, i, j + 1);

    return 1 + Math.max(0, up) + Math.max(0, down) + Math.max(0, left) + Math.max(0, right);
  }
}
