class Solution {
    public int minDays(int[][] grid) {

        int m=grid.length;
        int n=grid[0].length;

        int islandCount= findIslandCount(grid);

        if(islandCount==0 || islandCount>1) //means its already disconnected
            return 0;

        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                //make one cell to 0 and find the island count
                if(grid[i][j]==1){
                    grid[i][j]=0;
                    islandCount= findIslandCount(grid);
                    //make it again to 1
                    grid[i][j]=1;
                    if(islandCount==0 || islandCount>1) //means its already disconnected
                        return 1;
                }

            }
        }
        return 2;
    }

    private int findIslandCount(int[][] grid){

         int m=grid.length;
         int n=grid[0].length;
         boolean visited[][]=new boolean[m][n];
         int count=0;

        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(!visited[i][j] && grid[i][j]==1){
                    count++;
                    if(count>1) //if count >1 then we dont need to do dfs for more nodes
                        return count;
                     doDFS(grid,visited,i,j,m,n);  
                }
            }
        }
      return count;
    }

    private void doDFS(int[][] grid,boolean[][] visited,int i,int j,int m,int n){

        if(i<0 || j<0 || i>=m || j>=n || visited[i][j] || grid[i][j]!=1)
            return;

        visited[i][j]=true;

        doDFS(grid,visited,i+1,j,m,n);        
        doDFS(grid,visited,i-1,j,m,n);
        doDFS(grid,visited,i,j+1,m,n);
        doDFS(grid,visited,i,j-1,m,n);

    }
}

