class Solution {
private:
    int dir[4][2] = {{1,0}, {-1,0}, {0,1}, {0,-1}}; 

    bool valid(int i, int j, int m, int n) {
        return i >= 0 && i < m && j >= 0 && j < n;
    }

    void dfs(int x, int y, vector<vector<int>> &grid, vector<vector<bool>> &used) {
        used[x][y] = true;
        int m = grid.size();
        int n = grid[0].size();

        for(int i = 0; i < 4; i++) {
            int a = x + dir[i][0];
            int b = y + dir[i][1];
            if(valid(a, b, m, n) && grid[a][b] == 1 && !used[a][b])
                dfs(a, b, grid, used);
        }

    }

    int total_cmpnt(vector<vector<int>> &grid) {
        int m = grid.size();
        int n = grid[0].size();
        vector<vector<bool>> used(m, vector<bool>(n, false));

        int cnt = 0;
        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                if(grid[i][j] == 1 && !used[i][j]) {
                    cnt += 1;
                    dfs(i, j, grid, used);
                }
            }
        }

        return cnt;
    }

public:
    int minDays(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();

        // if initially disconnected
        int cnt = total_cmpnt(grid);

        // cout << cnt << endl;

        if(cnt != 1)
            return 0;

        // for 1 day
        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                if(grid[i][j] == 1) {
                    grid[i][j] = 0;   // flip to 0
                    cnt = total_cmpnt(grid);
                    if(cnt != 1)
                        return 1;
                    grid[i][j] = 1;   // restore to 1
                }
            }
        }

        return 2;
    }
};


